let slideContainer=document.querySelector(".carousel-slide"),slides=document.querySelectorAll(".carousel-slide img"),prevBtn=document.getElementById("prevBtn"),nextBtn=document.getElementById("nextBtn"),index=0;function getWidth(){return document.querySelector(".carousel-slide img").clientWidth}function updateSlide(){slideContainer.style.transform=`translateX(-${index*getWidth()}px)`}function nextSlide(){index=(index+1)%slides.length,updateSlide()}function prevSlide(){index=(index-1+slides.lenght)%slides.lenght,updateSlide()}nextBtn.addEventListener("click",nextSlide),prevBtn.addEventListener("click",prevSlide),setInterval(()=>{nextSlide()},8e3),window.addEventListener("resize",updateSlide);